@use '../base/mixins' as *;

.projects {
  &__container {
    display: grid;
    grid-template-columns: 1fr 3rem;
    grid-template-rows: auto;
    grid-template-areas:
      "card card"
      "title icon"
      "info .";

    @include breakpoint-up(medium) {
      // max-width: 450px;
      grid-template-columns: repeat(2, 1fr);
      grid-template-areas:
        "card title"
        "card info"
        "card link"
        "card .";

      grid-gap: 0 1em;

      &:nth-of-type(2n) {
        transform: scaleX(-1);
        // margin-left: auto;
      }
    }
  }

  &__card {
    grid-area: card;

    @include breakpoint-up(medium) {
      &:nth-of-type(n + 1) {
        transform: scaleX(-1);
      }
    }

    a {
      display: grid;
      place-items: center;
      max-width: 90%;
    }

    &__screenshot {
      source,
      img {
        display: block;
        max-width: 100%;
        height: auto;
      }
    }
  }

  &__title {
    grid-area: title;
    grid-column: 1 / span 2;

    @include breakpoint-up(medium) {
      grid-column: initial;
    }

    a {
      text-decoration: none;
    }
  }

  &__link {
    grid-area: icon;
    font-size: 0;
    letter-spacing: 0;

    @include breakpoint-up(medium) {
      grid-area: link;
    }

    svg {
      width: 2.5rem;
      height: 3rem;
    }
  }

  &__info {
    grid-area: info;
  }
}

