    margin: 2rem 1rem 3rem;

    .cta::before {
      background-color: var(--bg-color);
    }
    .cta:hover + .corners,
    .cta:hover + .corners + .corners {
      border-color: var(--bg-color);
    }
  }
  &__corners {
    &.top-block {
      top: -8px;
      left: 86px;

      &::before {
        right: -5px;
      }
    }

    &.bottom-block {
      left: -8px;
    }
  }
  &__corners,
  &__corners::before {
    background-color: var(--container-bg-color);
  }

  input,
  textarea {
    background-color: var(--container-bg-color);
    appearance: none;
    border: none;
    font-size: 2rem;
    color: var(--bg-color);

    &:focus {
      outline: 0;
    }
  }

  input::placeholder,
  textarea::placeholder {
    color: var(--bg-color);
    opacity: 1;
    font-size: 2rem;
    font-weight: 500;
    letter-spacing: 1px;
  }

  input:focus-within,
  input:focus-within::placeholder,
  textarea:focus-within,
  textarea:focus-within::placeholder {
    color: var(--primary-color);
  }

  &__border {
    position: relative;
    left: 0;
    bottom: 0;
    height: 0.2rem;
    width: 100%;
    margin-top: -2rem;
    background-color: var(--bg-color);
    transition: all var(--duration-medium) var(--basic-timing);

    &::before {
      content: "";
      position: absolute;
      left: 0;
      width: 0;
      height: 100%;
      background-color: var(--primary-color);
      transition: all var(--duration-medium) var(--basic-timing);
    }
  }

  input:focus-within + .contact__form__border::before,
  textarea:focus-within + .contact__form__border::before {
    width: 100%;
  }
}

