sources.scss/base/_mixins.scss:
// place mixins for color themes, pseudo-elements, and media queries
@use 'sass:map';

// Breakpoints for Media Queries
$breakpoints: (
  // 480px, 640px, 768px, 1024px, 1200px, 1400px
  xsmall: 30em,
  small: 40em,
  medium: 48em,
  large: 64em,
  xlarge: 75em,
  xxlarge: 87.5em
);

@mixin breakpoint-up($size) {
  @media only screen and (min-width: map-get($breakpoints, $size)) {
    @content;
  }
}

sources.scss/base/_root.scss:
@use '../base/mixins' as *;

:root {
  // Colors
  --bg-color: hsl(210, 39%, 16%);
  --preloader-bg-color: hsl(208, 45%, 14.85%);
  --container-bg-color: hsl(188, 50%, 20%);
  --decoration-color: hsl(209, 39%, 19%);
  --primary-color: hsl(168, 100%, 50%);
  --text-color: hsl(38, 22%, 90%);
  --selection-color: hsl(336, 80%, 50%);
  --scrollbar-hover-color: hsl(168, 100%, 30%);

  // Fallback fonts
  --fallback-font: -apple-system, BlinkMacSystemFont, "Segoe UI", "Robot",
    "Oxygen", "Ubuntu", "Cantarell", "Fira Sans", "Droid Sans", "Helvetica Neue",
    sans-serif;

  // Transitions and Animations
  // look into cubic beziers for custom timings
  --basic-timing: ease-in-out;
  --duration-short: 250ms;
  --duration-medium: 400ms;

  font-size: 10px;
}

body {
  background-color: var(--bg-color);
  position: relative;
  overflow-x: hidden;

  &::before {
    content: "";
    background-image: url("/assets/background.gif");

    position: fixed;
    width: 100%;
    height: 100%;
    opacity: 0.03;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    z-index: 999;
    will-change: auto;
    pointer-events: none;

    @media (prefers-reduced-motion) {
      background-image: none;
    }
  }
}

// Hiding content on viewports smaller than tablet
.hide-mobile {
  display: none;
  @include breakpoint-up(medium) {
    display: initial;
  }
}

.hide-desktop {
  @include breakpoint-up(large) {
    display: none;
  }
}

// Showing content on viewports larger than tablet
.show-desktop {
  display: none;
  @include breakpoint-up(large) {
    display: initial;
  }
}

// Spacing
.side-spacer {
  padding: 0 3rem;

  @include breakpoint-up(xsmall) {
    padding: 0 6rem;
  }

  @include breakpoint-up(medium) {
    padding: 0 3rem;
  }
}

.element-spacer {
  margin-bottom: 6rem;

  @include breakpoint-up(medium) {
    margin-bottom: 8rem;
  }

  @include breakpoint-up(large) {
    margin-bottom: 10rem;
  }
}

.headline-spacer {
  margin-bottom: 0.25rem;

  @include breakpoint-up(medium) {
    margin-bottom: 0.5rem;
  }
}

// Sizing

// Margin bottom can be experimented with
.section-container {
  margin: 8rem auto;
  max-width: min(640px, 100%);

  @include breakpoint-up(medium) {
    margin-bottom: 16rem;
    max-width: min(900px, 100%);
  }

  @include breakpoint-up(large) {
    margin-bottom: 20rem;
    max-width: min(1000px, 100%);
  }

  @include breakpoint-up(xlarge) {
    margin-bottom: 24rem;
    max-width: min(1100px, 100%);
  }
}

sources.scss/layouts/_hero.scss:
@use '../base/mixins' as *;

.hero {
  display: flex;
  flex-direction: column;

  @include breakpoint-up(medium) {
    display: grid;
    grid-template-columns: 1fr 1fr;
    grid-template-rows: auto;
    grid-template-areas:
      "headline image"
      "info image"
      "info2 image"
      "info3 image"
      "info4 image"
      "button image";
    grid-gap: 1em 3em;
    margin-top: 10rem;
  }

  @include breakpoint-up(large) {
    grid-gap: 1em 7em;
    margin-top: 16rem;
  }

  &__picture__container {
    grid-area: image;
    margin-bottom: 4rem;

    @include breakpoint-up(medium) {
      margin-bottom: 4rem;
      place-self: center;
    }
  }

  &__picture {
    object-fit: cover;
  }

  &__headline {
    line-height: 1;
    grid-area: headline;
    position: relative;

    &--headline {
      margin: 0.25rem 0 0 -0.4rem;
      font-size: 5rem;

      &:last-of-type {
        margin: 0 0 2rem -0.4rem;

        @include breakpoint-up(medium) {
          margin-bottom: 0;
        }
      }
    }

    &--subheadline {
      font-size: 1.8rem;
      font-family: "Kanit", var(--fallback-font);
      font-weight: 800;
      letter-spacing: 0.2rem;
      color: var(--text-color);
      text-transform: uppercase;
    }
  }

  &__introduction {
    &:nth-of-type(1) {
      grid-area: info;

      @include breakpoint-up(medium) {
        margin-top: 1rem;
      }
    }
    &:nth-of-type(2) {
      grid-area: info2;
    }
    &:nth-of-type(3) {
      grid-area: info3;
    }
    &:nth-of-type(4) {
      grid-area: info4;
    }

    &:not(:last-of-type) {
      margin-bottom: 2rem;

      @include breakpoint-up(medium) {
        margin-bottom: 0;
      }
    }
  }
  &__button {
    grid-area: button;
    width: max-content;

    button {
      margin: 5rem 0 0 2rem;

      @include breakpoint-up(medium) {
        margin: 2rem 0 0 2rem;
      }

      @include breakpoint-up(xlarge) {
        margin: 2rem 0 -0.5rem 2rem;
      }
    }
  }
}
// GSAP reveal animation
.headline-reveal {
  display: block;

  &:after {
    content: " ";
    position: absolute;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 1;
    transform-origin: 0 0;
    background: #192939;
  }
}

sources.scss/layouts/_skills.scss:
@use '../base/mixins' as *;

.skills {
  display: grid;

  &__container {
    padding: 2rem 0 2rem 2rem;

    @include breakpoint-up(medium) {
      padding: 2rem 1rem 2rem 3rem;
      &--proficient {
        margin-right: auto;
      }
      &--learning {
        margin-left: auto;
      }
    }

    @include breakpoint-up(large) {
      padding: 4rem 4rem 4rem 6rem;
    }

    &__headline {
      text-transform: uppercase;
      position: relative;
      display: inline-block;
      font-size: 1.6rem;
      margin-bottom: 1.5rem;

      @include breakpoint-up(medium) {
        font-size: 1.8rem;
      }

      &::after {
        content: "_";
        position: absolute;
        top: -1px;
        // create an animation to make this blink -- can just transition opacity
      }
    }
  }

  &__list {
    display: flex;
    flex-wrap: wrap;
    gap: 3rem;
    margin-right: 2.5rem;
    padding: 0;
    align-self: center;

    &__items {
      width: 40px;
      height: 40px;
      fill: var(--primary-color);
    }
  }
}

sources.scss/layouts/_projects.scss:
@use '../base/mixins' as *;

.projects {
  &__container {
    display: grid;
    grid-template-columns: 1fr 3rem;
    grid-template-rows: auto;
    grid-template-areas:
      "card card"
      "title icon"
      "info .";

    @include breakpoint-up(medium) {
      // max-width: 450px;
      grid-template-columns: repeat(2, 1fr);
      grid-template-areas:
        "card title"
        "card info"
        "card link"
        "card .";

      grid-gap: 0 1em;

      // &:nth-of-type(2n) {
      //   grid-template-areas:
      //     "title card"
      //     "info card"
      //     "link card"
      //     ". card";

      //   & .projects__card {
      //     transform: scaleX(-1);

      //     & .projects__card__screenshot {
      //       transform: scaleX(-1);
      //     }
      //   }
      // }
    }

    @include breakpoint-up(large) {
      grid-gap: 0 3em;
    }
  }

  &__card {
    grid-area: card;
    position: relative;

    a {
      display: grid;
      place-items: center;
      max-width: 90%;
    }

    &__screenshot {
      source,
      img {
        display: block;
        max-width: 100%;
        height: auto;
      }
    }
  }

  &__title {
    grid-area: title;
    grid-column: 1 / span 2;

    @include breakpoint-up(medium) {
      grid-column: initial;
    }
  }
  &__links {
    grid-area: icon;
    // grid-row: span 2;
    // align-self: end;
    margin: 0 0 1rem 0.5rem;

    @include breakpoint-up(medium) {
      grid-area: link;
      grid-row: span 2;
      align-self: end;
      margin: 0 0 -1rem 0.5rem;
    }

    @include breakpoint-up(large) {
      margin: 0 0 1rem 0.5rem;
    }

    & .project__button:nth-of-type(2) {
      margin-left: 4rem;
    }
  }

  &__link {
    grid-area: icon;
    font-size: 0;
    letter-spacing: 0;

    @include breakpoint-up(medium) {
      grid-area: link;
      grid-row: span 2;
      align-self: end;
    }

    @include breakpoint-up(large) {
      font-size: initial;
      letter-spacing: initial;
      text-decoration: none;
    }

    svg {
      width: 2.5rem;
      height: 3rem;
    }
  }

  &__info {
    grid-area: info;
  }
}

// GSAP reveal animation
.project-reveal-em {
  display: block;
  position: relative;
  overflow: hidden;

  &:after {
    content: " ";
    position: absolute;
    display: block;
    left: 0;
    width: 110%;
    height: 110%;
    z-index: 1;
    transform-origin: 100% 0;
    background: #192939;
  }
}
.project-reveal-hch {
  display: block;
  position: relative;
  overflow: hidden;

  &:after {
    content: " ";
    position: absolute;
    display: block;
    left: 0;
    width: 110%;
    height: 110%;
    z-index: 1;
    transform-origin: 100% 0;
    background: #192939;
  }
}

sources.scss/layouts/_modal.scss:
@use '../base/mixins' as *;

.menu {
  &__modal {
    position: fixed;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;
    z-index: 900;
    pointer-events: none;

    &--inner.modal--open,
    &--outer.modal--open {
      pointer-events: initial;
    }
  }
  &__modal--outer {
    background: hsla(210, 39%, 16%, 0.8);
    opacity: 0;
  }
  &__modal--inner {
    background-color: var(--container-bg-color);
    z-index: 900;
    opacity: 0;
    pointer-events: none;
    transition: all var(--duration-short) var(--basic-timing);
    display: flex;
    flex-direction: column;
    justify-content: center;

    @include breakpoint-up(large) {
      top: 50%;
      left: 50%;
      height: 550px;
      width: 850px;
      transform: translate3d(-50%, -50%, 0);
      flex-direction: row;
      align-items: center;
      justify-content: space-evenly;
    }

    @include breakpoint-up(xlarge) {
      max-height: 600px;
      max-width: 900px;
    }
  }

  &__socials {
    order: 2;
    margin-top: 4rem;

    @include breakpoint-up(large) {
      max-width: 200px;
      order: 1;
      display: flex;
      margin-top: revert;
    }

    &__list {
      display: flex;
      align-items: center;
      justify-content: space-between;
      gap: 3rem;

      @include breakpoint-up(xsmall) {
        justify-content: initial;
      }

      @include breakpoint-up(medium) {
        max-width: 500px;
        margin: 0 auto;
        padding: 0 0 0 1rem;
        justify-content: start;
      }

      @include breakpoint-up(large) {
        // max-width: 200px;
        padding: 0;
        flex-direction: column;
        gap: 6rem;
      }
    }

    &__items svg {
      width: 2.5rem;
      height: 2.5rem;
    }

    &__items a {
      text-decoration: none;
    }

    &__items:not(:last-of-type) {
      font-size: 0;
    }
    &__items:last-of-type a {
      font-family: "Kanit", var(--fallback-font);
      font-weight: 700;
      font-size: 3rem;
      display: inline-block;
      -webkit-text-fill-color: transparent;
      -webkit-text-stroke-color: var(--primary-color);
      -webkit-text-stroke-width: 1px;
      letter-spacing: 1.75px;
      text-transform: lowercase;
      padding-top: 0.1rem;

      @include breakpoint-up(medium) {
        padding-top: 0;
        -webkit-text-stroke-width: 0.75px;

        @-moz-document url-prefix() {
          -webkit-text-stroke-width: 0.8px;
        }
      }
    }
  }

  &__form {
    order: 1;

    @include breakpoint-up(large) {
      order: 2;
    }
  }
}

body.prevent-scroll {
  padding-right: 0.4rem;
  overflow: hidden;

  @-moz-document url-prefix() {
    padding-right: 0.75rem;
  }
}

