@use '../base/mixins' as *;

.menu__button {
  position: fixed;
  top: 1.5rem;
  right: 2rem;
  height: 4rem;
  width: 4rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 0.6rem;
  z-index: 950;
  cursor: pointer;

  @include breakpoint-up(medium) {
    top: 2rem;
    right: 3rem;
  }

  @include breakpoint-up(large) {
    top: 4rem;
    right: 5rem;
  }

  &:hover span,
  &:focus-within span,
  &:active span {
    background-color: var(--selection-color);
  }

  span {
    display: inline-block;
    background-color: var(--text-color);
    width: 2.5rem;
    height: 2px;
    align-self: flex-end;
    transition: all var(--duration-short) var(--basic-timing);

    &:nth-of-type(2) {
      width: 1.85rem;
    }
    &:nth-of-type(3) {
      width: 1.2rem;
    }
  }
  &.menu--open {
    transform: translate3d(-3px, -3px, 0);

    @include breakpoint-up(large) {
      top: 50%;
      left: 50%;
      transform: translate3d(365px, -267px, 0) !important;
    }

    span {
      &:nth-of-type(1) {
        // TODO: optimize these transforms?
        transform: rotate(-135deg) translate3d(-7px, -9.5px, 0);
      }
      &:nth-of-type(2) {
        opacity: 0;
      }
      &:nth-of-type(3) {
        width: 2.5rem;
        transform: rotate(-45deg) translate3d(2px, -4.5px, 0);
      }
    }
    &:hover,
    &:focus-within,
    &:active {
      span {
        background-color: var(--selection-color);
      }
    }
  }
}

sources.scss/components/_form.scss:
@use '../base/mixins' as *;

.menu__form {
  display: flex;
  flex-direction: column;
  gap: 1rem;

  @include breakpoint-up(medium) {
    align-self: center;
  }
}

.form__headline {
  font-size: 4.5rem;
  margin-bottom: -1rem;
}

.form__headline--subheadline {
  font-size: 1.6rem;
  margin-bottom: 4rem;

  @include breakpoint-up(medium) {
    font-size: 1.8rem;
  }
}

form {
  display: flex;
  flex-direction: column;
  gap: 2rem;
  min-width: 100%;
}

.contact__form {
  &__button {
    margin: 2rem 1rem 3rem;

    .cta::before {
      background-color: var(--bg-color);
    }
    .cta:hover + .corners,
    .cta:hover + .corners + .corners {
      border-color: var(--bg-color);
    }
  }
  &__corners {
    &.top-block {
      top: -8px;
      left: 86px;

      &::before {
        right: -5px;
      }
    }

    &.bottom-block {
      left: -8px;
    }
  }
  &__corners,
  &__corners::before {
    background-color: var(--container-bg-color);
  }

  input,
  textarea {
    background-color: var(--container-bg-color);
    appearance: none;
    border: none;
    font-size: 2rem;
    color: var(--bg-color);

    &:focus {
      outline: 0;
    }
  }

  input::placeholder,
  textarea::placeholder {
    color: var(--text-color);
    opacity: 1;
    font-size: 2rem;
    font-weight: 500;
    letter-spacing: 1px;
    transition: color var(--duration-medium) var(--basic-timing);
  }

  input:focus-within,
  input:focus-within::placeholder,
  textarea:focus-within,
  textarea:focus-within::placeholder {
    color: var(--primary-color);
  }

  &__border {
    position: relative;
    left: 0;
    bottom: 0;
    height: 0.2rem;
    width: 100%;
    margin-top: -2rem;
    background-color: var(--text-color);
    transition: all var(--duration-medium) var(--basic-timing);

    &::before {
      content: "";
      position: absolute;
      left: 0;
      width: 0;
      height: 100%;
      background-color: var(--primary-color);
      transition: all var(--duration-medium) var(--basic-timing);
    }
  }

  input:focus-within + .contact__form__border::before,
  textarea:focus-within + .contact__form__border::before {
    width: 100%;
  }
}

