sources.scss/components/_glitch-img.scss:
@use '../base/mixins' as *;

.glitch {
  position: relative;
  overflow: hidden;
  height: 400px;
  height: 450px;
  width: 100%;
  justify-self: end;
  z-index: 10;

  @include breakpoint-up(xsmall) {
    height: 600px;
    width: calc(100% +3rem);
  }

  @include breakpoint-up(small) {
    height: 700px;
  }

  @include breakpoint-up(medium) {
    height: calc(34vmax * 1.25);
    width: 30vmax;
  }

  @include breakpoint-up(large) {
    height: calc(40vmax * 1.25);
    width: 35vmax;
  }

  --gap-horizontal: 50px;
  --gap-vertical: 70px;
  --time-anim: 2.25s;
  --blend-mode-none: none;
  --blend-mode-overlay: overlay;
  --blend-color-transparent: transparent;
  --blend-color-primary: hsl(168, 100%, 30%, 0.7);
  --blend-color-secondary: hsl(336, 80%, 50%, 0.4);
}

.glitch__img {
  position: absolute;
  top: calc(-1 * var(--gap-vertical));
  left: calc(-1 * var(--gap-horizontal));
  width: calc(100% + var(--gap-horizontal) * 2);
  height: calc(100% + var(--gap-vertical) * 2);
  background: url("/assets/personal-image.png") no-repeat center center;
  background-size: cover;
  transform: translate3d(0, 0, 0);
}

.glitch__img:nth-child(2) {
  background-color: var(--blend-color-none);
  background-blend-mode: var(--blend-mode-none);
}

.glitch__img:nth-child(3) {
  background-color: var(--blend-color-primary);
  background-blend-mode: var(--blend-mode-overlay);
}

.glitch__img:nth-child(4) {
  background-color: var(--blend-color-secondary);
  background-blend-mode: var(--blend-mode-overlay);
}

.glitch__img:nth-child(5) {
  background-color: var(--blend-color-transparent);
  background-blend-mode: var(--blend-mode-none);
}

.glitch__img:nth-child(n + 2) {
  opacity: 0;
}

.glitch:hover .glitch__img:nth-child(n + 2) {
  opacity: 1;
}

.glitch:hover .glitch__img:nth-child(2) {
  transform: translate3d(var(--gap-horizontal), 0, 0);
  animation: glitch-anim-medium var(--time-anim) infinite linear alternate;

  @media (prefers-reduced-motion) {
    animation: none;
  }
}

.glitch:hover > .glitch__img:nth-child(3) {
  transform: translate3d(calc(-1 * var(--gap-horizontal)), 0, 0);
  animation: glitch-anim-large var(--time-anim) infinite linear alternate;

  @media (prefers-reduced-motion) {
    animation: none;
  }
}

.glitch:hover > .glitch__img:nth-child(4) {
  transform: translate3d(0, calc(-1 * var(--gap-vertical)), 0)
    scale3d(-1, -1, 1);
  animation: glitch-anim-small var(--time-anim) infinite linear alternate;

  @media (prefers-reduced-motion) {
    animation: none;
  }
}

.glitch:hover > .glitch__img:nth-child(5) {
  animation: glitch-anim-flash 2s steps(1, end) infinite;

  @media (prefers-reduced-motion) {
    animation: none;
  }
}

@keyframes glitch-anim-flash {
  0% {
    opacity: 0.2;
    transform: translate3d(
      (calc(-1 * var(--gap-horizontal))),
      var(--gap-vertical),
      0
    );
  }
  33%,
  100% {
    opacity: 0;
    transform: translate3d(0, 0, 0);
  }
}

@keyframes glitch-anim-small {
  0% {
    -webkit-clip-path: polygon(0 1%, 100% 1%, 100% 3%, 0 3%);
    clip-path: polygon(0 1%, 100% 1%, 100% 3%, 0 3%);
  }
  5% {
    -webkit-clip-path: polygon(0 10%, 100% 10%, 100% 9%, 0 9%);
    clip-path: polygon(0 10%, 100% 10%, 100% 9%, 0 9%);
  }
  10% {
    -webkit-clip-path: polygon(0 5%, 100% 5%, 100% 6%, 0 6%);
    clip-path: polygon(0 5%, 100% 5%, 100% 6%, 0 6%);
  }
  25% {
    -webkit-clip-path: polygon(0 20%, 100% 20%, 100% 20%, 0 20%);
    clip-path: polygon(0 20%, 100% 20%, 100% 20%, 0 20%);
  }
  27% {
    -webkit-clip-path: polygon(0 10%, 100% 10%, 100% 10%, 0 10%);
    clip-path: polygon(0 10%, 100% 10%, 100% 10%, 0 10%);
  }
  30% {
    -webkit-clip-path: polygon(0 30%, 100% 30%, 100% 25%, 0 25%);
    clip-path: polygon(0 30%, 100% 30%, 100% 25%, 0 25%);
  }
  33% {
    -webkit-clip-path: polygon(0 15%, 100% 15%, 100% 16%, 0 16%);
    clip-path: polygon(0 15%, 100% 15%, 100% 16%, 0 16%);
  }
  37% {
    -webkit-clip-path: polygon(0 40%, 100% 40%, 100% 39%, 0 39%);
    clip-path: polygon(0 40%, 100% 40%, 100% 39%, 0 39%);
  }
  40% {
    -webkit-clip-path: polygon(0 20%, 100% 20%, 100% 21%, 0 21%);
    clip-path: polygon(0 20%, 100% 20%, 100% 21%, 0 21%);
  }
  45% {
    -webkit-clip-path: polygon(0 60%, 100% 60%, 100% 55%, 0 55%);
    clip-path: polygon(0 60%, 100% 60%, 100% 55%, 0 55%);
  }
  50% {
    -webkit-clip-path: polygon(0 30%, 100% 30%, 100% 31%, 0 31%);
    clip-path: polygon(0 30%, 100% 30%, 100% 31%, 0 31%);
  }
  53% {
    -webkit-clip-path: polygon(0 70%, 100% 70%, 100% 69%, 0 69%);
    clip-path: polygon(0 70%, 100% 70%, 100% 69%, 0 69%);
  }
  57% {
    -webkit-clip-path: polygon(0 40%, 100% 40%, 100% 41%, 0 41%);
    clip-path: polygon(0 40%, 100% 40%, 100% 41%, 0 41%);
  }
  60% {
    -webkit-clip-path: polygon(0 80%, 100% 80%, 100% 75%, 0 75%);
    clip-path: polygon(0 80%, 100% 80%, 100% 75%, 0 75%);
  }
  65% {
    -webkit-clip-path: polygon(0 50%, 100% 50%, 100% 51%, 0 51%);
    clip-path: polygon(0 50%, 100% 50%, 100% 51%, 0 51%);
  }
  70% {
    -webkit-clip-path: polygon(0 90%, 100% 90%, 100% 90%, 0 90%);
    clip-path: polygon(0 90%, 100% 90%, 100% 90%, 0 90%);
  }
  73% {
    -webkit-clip-path: polygon(0 60%, 100% 60%, 100% 60%, 0 60%);
    clip-path: polygon(0 60%, 100% 60%, 100% 60%, 0 60%);
  }
  80% {
    -webkit-clip-path: polygon(0 100%, 100% 100%, 100% 99%, 0 99%);
    clip-path: polygon(0 100%, 100% 100%, 100% 99%, 0 99%);
  }
  100% {
    -webkit-clip-path: polygon(0 70%, 100% 70%, 100% 71%, 0 71%);
    clip-path: polygon(0 70%, 100% 70%, 100% 71%, 0 71%);
  }
}

@keyframes glitch-anim-medium {
